import React, { useState } from 'react'
import './Auth.css'
import icon from '../../assets/icon.png'
import AboutAuth from './AboutAuth'
const Auth = () => {
  const [isSignup,setIsSignup]=useState(false)

  const handleSwitch = () => {
    setIsSignup(!isSignup)
  }

  return (
    <section class='auth-section'>
      {isSignup && <AboutAuth />}
<div class='auth-container-2'>
{!isSignup && < img src={icon}  height={'80px'} width={'80px'} alt='stack overflow' className='login-logo'/>}
<form>
  {
    isSignup &&( 
      <label htmlFor='name'>
      <h4>Display Name</h4>
      <input type="text" id='name' name='name'/>
      </label>
    )
  }
  <label htmlFor="email">
    <h4>Email</h4>
    <input type="email" name='email' id='email'/>
    </label>
    <label htmlFor="password">
      <div style={{ display:"flex", justifyContent:"space-between"}}>
    <h4>Password</h4>
    {!isSignup && <p style={{ color: '#007ac6' , fontsize:'13px'}}>Forget Password ?</p>}
    </div>
    <input type="password" name='password' id='password'/>
    { isSignup && <p style={{ color:"#666767" , fontsize:"13px"}}>Password must contain atleast eight<br />characters , including atleast 1letter and 1 number</p>}
    </label>
    {
      isSignup && (
        <label htmlFor='check'>
          <input type ="checkbox" id='check' />
        <p style={{ fontsize:"56px"}}>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Opt-in to receive occasional ,<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;product updates , user research invitations ,<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;company announcements , and digests.
        </p>
        </label>
      )
    }
    <button type='submit' className='auth-btn'>{isSignup ? 'Sign up':'Log in'}</button>
    {
      isSignup && (
        <p style={{ color:"#666767" , fontsize:"13px"}}>
          By clicking "Sign up", you agree to our
           <span style={{ color: '#007ac6'}}> terms of <br />services</span>,
           <span style={{ color: '#007ac6'}}>privacy policy </span>and
           <span style={{ color: '#007ac6'}}>cookie policy</span>
        </p>
      )
    }
</form>
<p>
  {isSignup ? 'already have an account ?':"Don't have an account ?"}
  <button type='button' className='handle-switch-btn' onClick={handleSwitch}>{isSignup ? "Log in" : 'sign up'}</button>
</p>
</div>
    </section>
  )
}

export default Auth
