import React,{ useState } from 'react'
import { useSelector , useDispatch } from 'react-redux'
import { useParams , Link, useNavigate, useLocation } from 'react-router-dom'
import moment from 'moment'
import copy from 'copy-to-clipboard'

import upVotes from '../../assets/sort-up.svg'
import downVotes from '../../assets/sort-down.svg'
import './Questions.css'
import Avatar from '../../component/Avatar/Avatar'
import DisplayAnswers from './DisplayAnswers'
import { postAnswer } from '../../actions/question'
import { deleteQuestion } from '../../actions/question'


const QuestionsDetails = () => {

    const { id } =useParams()
    const questionsList = useSelector(state => state.questionsReducer)
    console.log(questionsList)

    /*var questionsList =[{
        _id:'1',
        votes: 3,
        upVotes:3,
        downVotes:2,
        noOfAnswers:2,
        questionTitle:"What is a function?",
        questionBody:"It meant to be",
        questionTags:["java","node.js","react.js","mongodb","express.js"],
        userPosted:"mano",
        askedOn:"jan 1",
        answer:[{
            answerBody:"Answer",
            userAnswered: 'kumar',
            answeredOn: "jan 2",
            userId: 2 ,
        }]
  
  },{
    _id:'2',
        votes: 0,
        upVotes:3,
        downVotes:2,
        noOfAnswers:0,
        questionTitle:"What is a function?",
        questionBody:"It meant to be",
        questionTags:["javascript","R","python"],
        userPosted:"mano",
        askedOn:"jan 1",
        answer:[{
          answerBody:"Answer",
          userAnswered: 'kumar',
          answeredOn: "jan 2",
          userId: 2 ,
      }]
  
  },{
    _id:'3',
        votes: 1,
        upVotes:3,
        downVotes:2,
        noOfAnswers:0,
        questionTitle:"What is a function?",
        questionBody:"It meant to be",
        questionTags:["javascript","R","python"],
        userPosted:"mano",
        askedOn:"jan 1",
        answer:[{
          answerBody:"Answer",
          userAnswered: 'kumar',
          answeredOn: "jan 2",
          userId: 2 ,
      }]
  
  }]*/
  const [Answer, setAnswer] = useState('')
  const Navigate = useNavigate()
  const dispatch =useDispatch()
  const User = useSelector((state)=> (state.currentUserReducer))
  const location =useLocation()
  console.log(location)
  const url ='http://localhost:3000'
  
  const handlePostAns = (e,answerLength) =>{
    e.preventDefault()
    if(User === null){
        alert('Login or Signup to answer a question ')
        Navigate('/Auth')
} else{
    if(Answer === ''){
        alert('Enter an answer before submitting')
    }else{
        dispatch(
            postAnswer({
              id,
              noOfAnswers: answerLength + 1,
              answerBody: Answer,
              userAnswered: User.result.name, userId: User.result._id}))
    }
}
}

  const handleShare = () => {
        copy(url+location.pathname)
        alert('Copied url :'+url+location.pathname)
  }
 

  const handleDelete = () => {
    dispatch(deleteQuestion(id, Navigate));
  };

    const handleUpVote =() => {
        dispatch(voteQuestion(id, 'upvote', User.result._id));
    }

    const handleDownVote =() => {
        dispatch(voteQuestion(id, 'downvote', User.result._id));
    }


  return (
    <div className='question-detail-page'>
        {
questionsList.data ===null ?
<h1>loading......</h1>:
            <>
            {
                questionsList.data.filter(question => question._id === id).map(question =>(
                    <div key={question._id}>
                        {console.log(question)}
                     <section className='question-details-container'>
                    <h1>{question.questionTitle}</h1>
                    <div className='question-details-container-2'>
                        <div className="question-votes">
                            <img src={upVotes}  alt="" width='18'  className='votes-icon' onClick={handleUpVote} />
                            <p>{question.upVotes - question.downVotes}</p>
                            <img src={downVotes}  alt="" width='18' className='votes-icon' onClick={handleDownVote} />
                        </div>
                        <div style={{width:"100%"}}>
                        <p className='question-body'>{question.questionBody}</p>
                        <div className="question-details-tags">
                            {
                                question.questionTags.map((tag)=>(
                                    <p key={tag}>{tag}</p>
                                ))
                            }
                        </div>

                        <div className="question-actions-user">
                        <div>
                        <button type='button' onClick={handleShare}>Share</button>
                        {
                            User?.result?._id === question?.userId && (
                                <button type='button' onClick={handleDelete}>Delete</button>
                            )
                        }
                        
                        </div>
                        
                        <div>
                            <p>asked {moment(question.postedOn).fromNow()}</p>
                            <Link to={`/User/${question.userId}`} className='user-link' style={{color:'#0086d8'}}>
                            <Avatar backgroundColor="orange" px='8px' py='5px'>{question.userPosted.charAt(0).toUpperCase()}</Avatar>
                            <div>
                                {question.userPosted}
                            </div>
                            </Link>
                        </div>
                        </div>
                        </div>
                    </div>
                     </section>
                     {
                        question.noOfAnswers !== 0 && (
                            <section>
                                <h3>{question.noOfAnswers} answers</h3>
                                <DisplayAnswers key={question._id} question={question} handleShare={handleShare}/>
                            </section>
                        )
                     }
                     <section className='post-ans-container'>
                        <h3>Your Answer</h3>
                     <form onSubmit={ (e) => {handlePostAns(e,question.answer.length) }}> 
                        <textarea name="" id="" cols="30" rows="10" onChange={e => setAnswer(e.target.value)}></textarea><br />
                        <input type="Submit" className='post-ans-btn' value='Post Your Answer' />
                    </form>
                     <p>
                        Browse other Question tagged
                        {
                            question.questionTags.map((tag)=> (
                                <Link to='/Tags' key={tag} className='ans-tags'> {tag} </Link>
                            ))
                        }or
                        <br /><Link to='/AskQuestion' style={{textDecoration:"none",color:"#009dff"}}>ask your own question.</Link>
                     </p>
                     </section>
                    </div>
                ))
            }
            </>
        }
    </div>
  )
}

export default QuestionsDetails
