import React from 'react'
import { Link } from 'react-router-dom'

const User = ({ user }) => {
  return (
    <div>
      <Link to={`/Users/${user._id}`} className='user-profile-link'> 
      <h3>{user.name.charAt(0).toUpperCase()}</h3>
      <h5>{user.name}</h5>

      </Link>
    </div>
  )
}

export default User
