import React from 'react'
import { useParams } from 'react-router-dom'
const QuestionsDetails = () => {

    const { id } =useParams()
    var questionsList =[{
        id:1,
        votes: 3,
        noOfAnswers:2,
        questionTitle:"What is a function?",
        questionBody:"It meant to be",
        questionTags:["java","node.js","mongodb"],
        userPosted:"mano",
        askedOn:"jan 1",
        answer:[{
            answerBody:"Answer",
            userAnswered: 'kumar',
            answeredOn: "jan 2",
            userId: 2 ,
        }]
  
  },{
    id:2,
        votes: 0,
        noOfAnswers:0,
        questionTitle:"What is a function?",
        questionBody:"It meant to be",
        questionTags:["javascript","R","python"],
        userPosted:"mano",
        askedOn:"jan 1",
        answer:[{
          answerBody:"Answer",
          userAnswered: 'kumar',
          answeredOn: "jan 2",
          userId: 2 ,
      }]
  
  },{
    id:3,
        votes: 1,
        noOfAnswers:0,
        questionTitle:"What is a function?",
        questionBody:"It meant to be",
        questionTags:["javascript","R","python"],
        userPosted:"mano",
        askedOn:"jan 1",
        answer:[{
          answerBody:"Answer",
          userAnswered: 'kumar',
          answeredOn: "jan 2",
          userId: 2 ,
      }]
  
  }]

  return (
    <div className='question-detail-page'>
        {
            questionsList ===null ?
            <h1>loading......</h1>:
            <>
            {
                questionsList.filter(question => question._id === id)
            }
            </>
        }
    </div>
  )
}

export default QuestionsDetails
